//é‡æ’players
sort_players = key => {
	key = key || "scores";
	var players = _status.players;
	if (key == "money") {
		players.sort((a, b) => {
			if (a.money == b.money) {
				return a.id < b.id ? 1 : -1;
			}
			return a.money < b.money ? 1 : -1;
		});
	} else if (key == "scores") {
		players.sort((a, b) => {
			if (a.countScores() == b.countScores()) {
				return a.id < b.id ? 1 : -1;
			}
			return a.countScores() < b.countScores() ? 1 : -1;
		});
	}
};

